import React from "react";
import CustomButton from "../custom-button/custom-button.component";
import "./job-card.styles.css";

function JobCard (props) { 
const tags = [props.role, props.level, ...props.languages, props.tools]
return (
<div className="container">
  <div className="card">
    <div className="companyName">
      <img src={props.logo} alt="logo" width="100" height="100" />
    </div>
    <div className="content">
      <div className="newFeatured">
        <h2>{props.company}</h2>
        {props.newJob ? <button className="myButton">New!</button> : null}
        {props.featured ? <button className="myDarkButton">Featured</button> : null}
      </div>
      <div className="jobNames">
        <h1>{props.position}</h1>
        <div className="techNames">
          {

          tags &&
          tags.map((language, id) => (
          <CustomButton key={id}> {language}</CustomButton>
          ))}
        </div>
      </div>
      <div className="details">
        <h3>{props.postedAt} &#183;</h3>
        <h3>{props.contract} &#183;</h3>
        <h3>{props.location}</h3>
      </div>
    </div>
  </div>
</div>
);
}
// {
